import React, { useState } from "react";
import { Link } from "react-router-dom";
import { useLanguage } from "../LanguageSource";
import cli from "../img/cli.png";
import br from "../img/br.png";
import uk from "../img/map.png";
import { Typewriter } from "react-simple-typewriter";

const Navbar = () => {
  const { currentLanguage, translations, changeLanguage } = useLanguage();
  const [isOpen, setIsOpen] = useState(false); // State for mobile menu

  let hello = translations[currentLanguage].hello;
  let myNameIs = translations[currentLanguage].myNameIs;

  const handleLanguageChange = (lang) => {
    if (lang !== currentLanguage) {
      changeLanguage(lang);
    }
  };

  const toggleLanguage = () => {
    return currentLanguage === "en" ? "pt" : "en";
  };

  return (
    <nav className="bg-mainBlue">
      <div className="max-w-7xl mx-auto px-2 sm:px-6 lg:px-8">
        <div className="relative flex items-center justify-between h-16">
          {/* Logo */}
          <div className="absolute inset-y-0 left-0 flex items-center sm:hidden">
            <button
              type="button"
              className="inline-flex items-center justify-center p-2 rounded-md text-gray-400 hover:text-white hover:bg-mainBlue focus:outline-none focus:ring-2 focus:ring-inset focus:ring-white"
              aria-controls="mobile-menu"
              aria-expanded="false"
              onClick={() => setIsOpen(!isOpen)}
            >
              <span className="sr-only">Open main menu</span>
              {/* Hamburger Icon */}
              {!isOpen ? (
                <svg
                  className="block h-6 w-6"
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                  aria-hidden="true"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M4 6h16M4 12h16M4 18h16"
                  />
                </svg>
              ) : (
                <svg
                  className="block h-6 w-6"
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                  aria-hidden="true"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M6 18L18 6M6 6l12 12"
                  />
                </svg>
              )}
            </button>
          </div>
          <div className="flex-1 flex items-center justify-center sm:items-stretch sm:justify-start">
            {/* Logo */}
            <div className="flex-shrink-0">
              <Link to="/">
                <img
                  className="block lg:hidden h-8 w-auto"
                  src={cli}
                  alt="Workflow"
                />
                <img
                  className="hidden lg:block h-8 w-auto"
                  src={cli}
                  alt="Workflow"
                />
              </Link>
            </div>
            {/* Typewriter Effect */}
            <div className="hidden sm:block sm:ml-6">
              <div className="flex space-x-4">
                <Typewriter
                  words={[hello, myNameIs]}
                  loop={true}
                  cursor
                  cursorStyle="_"
                  typeSpeed={150}
                  deleteSpeed={50}
                  delaySpeed={1000}
                />
              </div>
            </div>
          </div>
          {/* Language Switch and Mobile Menu */}
          <div className="absolute inset-y-0 right-0 flex items-center sm:static sm:inset-auto sm:ml-6 sm:pr-0">
            {/* Language Switch */}
            <div className="hidden sm:block sm:ml-6">
              <div className="flex space-x-4">
                <img
                  alt={toggleLanguage()}
                  title={toggleLanguage() === "pt" ? "PortuguÃªs" : "English"}
                  onClick={() => handleLanguageChange(toggleLanguage())}
                  className="h-8 sm:h-10 hover:cursor-pointer hover:scale-110 border-2 border-transparent rounded-md"
                  src={currentLanguage === "en" ? br : uk}
                />
              </div>
            </div>
          </div>
        </div>
      </div>
      {/* Mobile Menu */}
      <div
        className={`${isOpen ? "block" : "hidden"} sm:hidden`}
        id="mobile-menu"
      >
        <div className="px-2 pt-2 pb-3 space-y-1">
          {/* Nav Links */}
          <Link
            to="/"
            className="text-gray-300 hover:bg-gray-700 hover:text-white block px-3 py-2 rounded-md text-base font-medium"
          >
            Home
          </Link>
          <Link
            to="/about"
            className="text-gray-300 hover:bg-gray-700 hover:text-white block px-3 py-2 rounded-md text-base font-medium"
          >
            About
          </Link>
          {/* Additional Links */}
          <a
            href="https://github.com/jeanmarquesvieira"
            target="_blank"
            rel="noopener noreferrer"
            className="text-gray-300 hover:bg-gray-700 hover:text-white block px-3 py-2 rounded-md text-base font-medium"
          >
            GitHub
          </a>
          <a
            href="https://www.linkedin.com/in/jeanmarquesvieira/"
            target="_blank"
            rel="noopener noreferrer"
            className="text-gray-300 hover:bg-gray-700 hover:text-white block px-3 py-2 rounded-md text-base font-medium"
          >
            LinkedIn
          </a>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
