import "../index.css";
import jmv from "../img/jmv.png";
import { useLanguage } from "../LanguageSource";
import React, { useState } from "react";
import salesforce from "../img/salesforce.png";
import js from "../img/js.png";
import react from "../img/react.png";
import nodejs from "../img/nodejs.png";
import htmlCss from "../img/html_css.png";
import tailwind from "../img/tailwind.png";
import c from "../img/C_programming.png";

const Home = () => {
  const { currentLanguage, translations } = useLanguage();
  const [technologies, showTechnologies] = useState(false);
  const [softSkills, showSoftSkills] = useState(false);

  const imagesSrc = [
    "react",
    "js",
    "c",
    "nodejs",
    "tailwind",
    "htmlCss",
    "salesforce",
  ];

  return (
    <>
      <div className="bg-gray-100 min-h-screen flex items-center justify-center overflow-hidden font-fira">
        <div className="flex flex-col md:flex-row w-full max-w-5xl mx-auto p-4">
          <div className="flex justify-center md:justify-start md:w-1/3 mb-4 md:mb-0">
            <div className="relative bottom-20 h-40 w-32 md:h-64 md:w-52 rounded-full overflow-hidden">
              <img
                src={jmv}
                alt="jmv"
                className="absolute inset-0 h-full w-full object-cover"
              />
            </div>
          </div>
          <div className="flex flex-col relative bottom-20 md:ml-4 md:w-2/3">
            <h1 className="text-xl sm:text-2xl md:text-3xl font-bold text-gray-800 mt-4 md:mt-0">
              {translations[currentLanguage].greeting}
            </h1>
            <p className="text-lg text-gray-700 mt-2 md:mt-4 text-justify tracking-wider flex-grow">
              {translations[currentLanguage].description}
            </p>
            <div className="flex justify-center md:justify-start mt-8 space-x-8">
              <button
                className="bg-bblue text-black active:bg-gray-100 p-2 px-3 rounded-xl font-medium transition duration-400 hover:scale-110 font-fira text-lg"
                onClick={() => showTechnologies(true)}
              >
                {translations[currentLanguage].technologies}
              </button>
              {showTechnologies ? (
                <div className="justify-center items-center flex fixed inset-0 z-50 outline-none focus:outline-none">
                  <div className="relative w-auto my-6 mx-auto max-w-sm">
                    <div className="border-0 rounded-lg shadow-lg relative flex flex-col w-full bg-white outline-none focus:outline-none">
                      <div className="flex items-start justify-between p-5 border-b border-solid border-blueGray-200 rounded-t">
                        <h3 className="text-3xl font-semibold">
                          {translations[currentLanguage].technologies}
                        </h3>
                        <button
                          className="p-1 ml-auto bg-transparent border-0 text-black opacity-5 float-right text-3xl leading-none font-semibold outline-none focus:outline-none"
                          onClick={() => showTechnologies(false)}
                        >
                          <span className="bg-transparent text-black opacity-5 h-6 w-6 text-2xl block outline-none focus:outline-none"></span>
                        </button>
                      </div>
                      {/*body*/}
                      <div className="relative p-6 flex-auto">
                        <div className="grid grid-cols-3 gap-2">
                          <img
                            src={react}
                            className=""
                            alt="react"
                            title="React"
                          ></img>
                          <img
                            src={react}
                            className=""
                            alt="react"
                            title="React"
                          ></img>
                          <img
                            src={react}
                            className=""
                            alt="react"
                            title="React"
                          ></img>
                          <img
                            src={react}
                            className=""
                            alt="react"
                            title="React"
                          ></img>
                        </div>
                        {/* <p className="my-4 text-blueGray-500 text-lg leading-relaxed">
                        </p> */}
                      </div>
                      {/*footer*/}
                      <div className="flex items-center justify-end p-6 border-t border-solid border-blueGray-200 rounded-b">
                        <button
                          className="text-red-500 background-transparent font-bold uppercase px-6 py-2 text-sm outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150"
                          type="button"
                          onClick={() => showTechnologies(false)}
                        >
                          Close
                        </button>
                        <button
                          className="bg-emerald-500 text-white active:bg-emerald-600 font-bold uppercase text-sm px-6 py-3 rounded shadow hover:shadow-lg outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150"
                          type="button"
                          onClick={() => showTechnologies(false)}
                        >
                          Save Changes
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              ) : //   <div className="opacity-25 fixed inset-0 z-40 bg-black"></div>
              null}
              <button
                className="bg-bblue text-black active:bg-gray-100 p-2 px-3 rounded-xl font-medium transition duration-400 hover:scale-110 font-fira text-lg"
                onClick={() => showSoftSkills(true)}
              >
                Soft Skills
              </button>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Home;
