// src/components/SearchAndPlay.js
import React, { useState } from "react";
import axios from "axios";
import { useLanguage } from "../LanguageSource";
import play from "../img/play.png";

const Spotify = () => {
  const { currentLanguage, translations } = useLanguage();
  const [query, setQuery] = useState("");
  const [trackUri, setTrackUri] = useState("");
  const [showMessage, setShowMessage] = useState(true);

  const beingPlayed = translations[currentLanguage].beingPlayed;

  const handleSearch = async () => {
    try {
      const response = await axios.get(`/search?q=${query}`);
      setTrackUri(response.data.uri);
    } catch (error) {
      console.error("Search Error:", error);
      //   setMessage("Error occurred during search");
    }
  };

  const handlePlay = async () => {
    try {
      await axios.get(`/play?uri=${trackUri}`);
      setShowMessage(true);
      //   setMessage({translations[currentLanguage].beingPlayed});
    } catch (error) {
      console.error("Play Error:", error);
      //   setMessage("Error occurred during playback");
    }
  };

  return (
    <div className="container mx-auto p-4 pt-28 font-fira">
      <h1 className="text-2xl font-bold mb-4 flex justify-center">
        Spotify Player
      </h1>
      <div className="mb-4 flex justify-center max-w-xl">
        <input
          type="text"
          className="border p-2 rounded-lg w-full"
          placeholder={translations[currentLanguage].searchForSong}
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
      </div>
      <div className="mb-4 flex justify-center">
        <button
          className="bg-mainBlue text-white py-2 px-4 rounded-lg"
          onClick={handleSearch}
        >
          {translations[currentLanguage].searchBtn}
        </button>
      </div>
      {trackUri && (
        <div className="mb-4 ">
          <img
            src={play}
            className="h-16 hover:cursor-pointer-events-auto"
            onClick={handlePlay}
            alt="play"
          />
        </div>
      )}
      {showMessage && <p>{beingPlayed}</p>}
    </div>
  );
};

export default Spotify;
