import "../index.css";
import jmv from "../img/jmv.png";
import { useLanguage } from "../LanguageSource";
import React, { useState } from "react";

const Home = () => {
  const { currentLanguage, translations } = useLanguage();
  const [softSkills, showSoftSkills] = useState(false);

  const languagesSrc = [
    react,
    javascript,
    c,
    nodejs,
    tailwind,
    html,
    css,
    salesforce,
  ];

  return (
    <>
      <div className="bg-gray-100 min-h-screen flex items-center justify-center overflow-hidden font-fira">
        <div className="flex flex-col md:flex-row w-full max-w-5xl mx-auto p-4">
          <div className="flex justify-center md:justify-start md:w-1/3 mb-4 md:mb-0">
            <div className="relative bottom-20 h-40 w-32 md:h-64 md:w-52 rounded-full overflow-hidden">
              <img
                src={jmv}
                alt="jmv"
                className="absolute inset-0 h-full w-full object-cover"
              />
            </div>
          </div>
          <div className="flex flex-col relative bottom-20 md:ml-4 md:w-2/3">
            <h1 className="text-xl sm:text-2xl md:text-3xl font-bold text-gray-800 mt-4 md:mt-0">
              {translations[currentLanguage].greetings}
            </h1>
            <p className="text-lg text-gray-700 mt-2 md:mt-4 text-justify tracking-wider flex-grow">
              {translations[currentLanguage].description}
            </p>
          </div>
        </div>
      </div>
    </>
  );
};

export default Home;
