import "../index.css";
import jmv from "../img/jmv.png";
import { useLanguage } from "../LanguageSource";

const Home = () => {
	const { currentLanguage, translations } = useLanguage();

	const description = "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Duis varius odio nec dui ullamcorper dignissim. Cras nec velit eu justo sodales ullamcorper.";

	const skills = [
	  "JavaScript",
	  "React",
	  "C",
	  "Node.js",
	  "HTML/CSS",
	  "Responsive Design",
	  "UI/UX Design",
	  "Salesforce",
	  "Lightning Web Components"
	];

	return (
	  <div className="bg-gray-100 min-h-screen py-8 px-4 sm:px-6 lg:px-8">
		<div className="max-w-3xl mx-auto bg-white shadow-md rounded-lg overflow-hidden">
		  <div className="flex items-center justify-center bg-gray-200 p-8">
			<div className="relative h-40 w-32 md:h-64 md:w-52 rounded-full overflow-hidden">
			  <img
				src={jmv}
				alt="jmv"
				className="absolute inset-0 h-full w-full object-cover"
			  />
			</div>
		  </div>
		  <div className="p-6">
			<h1 className="text-3xl font-bold text-gray-800 word">{translations[currentLanguage].greeting}</h1>
			<p className="text-lg text-gray-700 mt-2">{description}</p>
			<div className="mt-4">
			  <h2 className="text-xl font-semibold text-gray-800">Skills:</h2>
			  <ul className="mt-2">
				{skills.map((skill, index) => (
				  <li key={index} className="text-lg text-gray-700">{skill}</li>
				))}
			  </ul>
			</div>
		  </div>
		</div>
	  </div>
	);
  };

  export default Home;
