import "../index.css";
import jmv from "../img/jmv.png";
import { useLanguage } from "../LanguageSource";
import React, { useState } from "react";

const Home = () => {
  const { currentLanguage, translations } = useLanguage();
  const [technologies, showTechnologies] = useState(false);
  const [softSkills, showSoftSkills] = useState(false);

  const skills = [
    "JavaScript",
    "React",
    "C",
    "Node.js",
    "HTML/CSS",
    "Responsive Design",
    "UI/UX Design",
    "Salesforce",
    "Lightning Web Components",
  ];

  return (
    <>
      <div className="bg-gray-100 min-h-screen flex items-center justify-center py-8 px-4 sm:px-6 lg:px-10 font-fira">
        <div className="bg-white shadow-md rounded-lg overflow-hidden">
          <div className="flex items-center justify-center bg-gray-200 p-14 sm:p-20 md:p-24">
            <div className="relative h-40 w-32 md:h-64 md:w-52 lg:h-80 lg:w-60 rounded-full overflow-hidden">
              <img
                src={jmv}
                alt="jmv"
                className="absolute inset-0 h-full w-full object-cover"
              />
            </div>
          </div>
          <div className="p-6 lg:p-10">
            <h1 className="text-xl sm:text-2xl md:text-3xl font-bold text-gray-800 whitespace-nowrap">
              {translations[currentLanguage].greeting}
            </h1>
            <p className="text-lg text-gray-700 mt-2 text-justify tracking-wider">
              {translations[currentLanguage].description}
            </p>
            <div className="mt-6 flex justify-center space-x-6 md:justify-evenly sm:justify-around text-sm sm:text-md md:text-lg p-10">
              <button
                className="bg-bblue text-black active:bg-gray-100 p-2 px-4 whitespace-nowrap rounded-xl font-medium shadow hover:shadow-lg transition duration-400 hover:scale-110 font-fira"
                onClick={() => showTechnologies(true)}
              >
                {translations[currentLanguage].technologies}
              </button>
              <button
                className="bg-bblue text-black active:bg-gray-100 p-2 px-4 whitespace-nowrap rounded-xl font-medium shadow hover:shadow-lg transition duration-400 hover:scale-110 font-fira"
                onClick={() => showSoftSkills(true)}
              >
                Soft Skills
              </button>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Home;
