import React, { useState, useEffect, useRef } from "react";
import { useLanguage } from "../LanguageSource";
import salesforce from "../img/technologies/salesforce.png";
import javascript from "../img/technologies/js.png";
import react from "../img/technologies/react.png";
import nodejs from "../img/technologies/nodejs.png";
import html from "../img/technologies/html.png";
import css from "../img/technologies/css.png";
import tailwind from "../img/technologies/tailwind.png";
import c from "../img/technologies/C_programming.png";

const TechModal = () => {
  const { currentLanguage, translations } = useLanguage();
  const [showTechnologies, setShowTechnologies] = useState(false);

  const languagesSrc = [
    react,
    javascript,
    c,
    nodejs,
    tailwind,
    html,
    css,
    salesforce,
  ];

  useEffect(() => {
    function handleEscKey(e) {
      if (e.key === "Escape") {
        setShowTechnologies(false);
      }
    }

    document.addEventListener("keydown", handleEscKey);
    return () => document.removeEventListener("keydown", handleEscKey);
  }, []);

  const handleMouseClick = (e) => {
    const techRef = useRef();
    console.log(techRef);
    if (techRef.current && !techRef.current.contains(e.target)) {
      console.log("modal");
      setShowTechnologies(false);
    }
  };

  useEffect(() => {
    if (showTechnologies)
      document.addEventListener("mousedown", handleMouseClick);

    return () => document.removeEventListener("mousedown", handleMouseClick);
  }, [showTechnologies]);

  return (
    <>
      <div className="flex justify-center md:justify-start space-x-8 font-fira">
        <button
          className="bg-bblue text-black p-2 px-3 rounded-xl font-medium transition duration-400 hover:scale-110 font-fira text-lg"
          onClick={() => setShowTechnologies(true)}
        >
          {translations[currentLanguage].technologies}
        </button>
        {showTechnologies ? (
          <div className="justify-center items-center flex fixed inset-0 z-50 outline-none focus:outline-none ref={techRef}">
            <div className="relative w-auto my-6 mx-auto max-w-sm">
              <div className="border-0 rounded-lg shadow-lg relative flex flex-col w-full bg-white outline-none focus:outline-none">
                <div className="flex items-start justify-between p-5 border-b border-solid border-blueGray-200 rounded-t">
                  <h3 className="text-3xl font-semibold">
                    {translations[currentLanguage].technologies}
                  </h3>
                  <p>{translations[currentLanguage].subtitle}</p>
                  <button
                    className="p-1 ml-auto bg-transparent border-0 text-black opacity-5 float-right text-3xl leading-none font-semibold outline-none focus:outline-none"
                    onClick={() => {
                      console.log("clicked!");
                      setShowTechnologies(false);
                    }}
                  >
                    <span className="bg-transparent text-black opacity-5 h-6 w-6 text-2xl block outline-none focus:outline-none">
                      test
                    </span>
                  </button>
                </div>
                {/*body*/}
                <div className="relative p-6 flex-auto">
                  <div className="grid grid-cols-2 gap-2">
                    {languagesSrc.map((src, id) => (
                      <img
                        className="max-w-24"
                        key={id}
                        alt={`Logo ${src}`}
                        src={src}
                      />
                    ))}
                  </div>
                </div>
                <div className="flex items-center justify-end p-6 border-t border-solid border-blueGray-200 rounded-b">
                  <button
                    className="bg-bblue text-white font-bold uppercase text-sm px-6 py-3 rounded-lg shadow hover:shadow-lg outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150"
                    type="button"
                    onClick={() => setShowTechnologies(false)}
                  >
                    {translations[currentLanguage].close}
                  </button>
                </div>
              </div>
            </div>
          </div>
        ) : null}
      </div>
    </>
  );
};

export default TechModal;
