import React from "react";
import { Link } from "react-router-dom";
import { useLanguage } from "../LanguageSource";
import "../index.css";
import "../styles/navbar.css";
import terminal from "../img/terminal.png";
import br from "../img/br.png";
import uk from "../img/uk.png";

const Navbar = () => {
  const { currentLanguage, translations, changeLanguage } = useLanguage();

  const handleLanguageChange = (lang) => {
    changeLanguage(lang);
  };

  return (
    <nav className="block px-2 sm:px-3 md:px-4 lg:px-6 py-1 text-black bg-sky-600 shadow-2xl bg-opacity-95 backdrop-blur-2xl backdrop-saturate-200 overflow-hidden">
      <div className="flex items-center justify-between">
        <Link to="/">
          <img
            alt="Terminal"
            className="h-8 sm:h-10 m-h12 hover:cursor-pointer hover:scale-110 w-auto"
            src={terminal}
          />
        </Link>
        <ul className="flex flex-nowrap flex-1 justify-end my-0 gap-1 sm:gap-4 md:gap-6 lg:gap-8 mr-3">
          <li className="block p-1 sm:p-1 md:p-2 lg:p-3 font-medium leading-normal text-blue-gray-900">
            <Link
              className="cursor-pointer whitespace-nowrap text-sm sm:text-base md:text-lg tracking-tighter sm:tracking-wide flex items-center transition-colors rounded-lg px-1 sm:px-2 md:px-3 lg:px-4 hover:text-cyan-300 hover:bg-gray-700"
              to="/projects"
            >
              {translations[currentLanguage].projects}
            </Link>
          </li>
          <li className="block p-1 sm:p-1 md:p-2 lg:p-3 font-medium leading-normal text-blue-gray-900">
            <a
              className="cursor-pointer whitespace-nowrap text-sm sm:text-base md:text-lg tracking-tighter sm:tracking-wide flex items-center transition-colors rounded-lg px-1 sm:px-2 md:px-3 lg:px-4 hover:text-cyan-300 hover:bg-gray-700"
              href="https://github.com/jeanmarquesvieira"
              target="_blank"
              rel="noopener noreferrer"
            >
              GitHub
            </a>
          </li>
          <li className="block p-1 sm:p-1 md:p-2 lg:p-3 font-medium leading-normal text-blue-gray-900">
            <a
              className="cursor-pointer whitespace-nowrap text-sm sm:text-base md:text-lg tracking-tighter sm:tracking-wide flex items-center transition-colors rounded-lg px-1 sm:px-2 md:px-3 lg:px-4 hover:text-cyan-300 hover:bg-gray-700 mr-2 sm:mr-4 md:mr-6 lg:mr-8"
              href="https://www.linkedin.com/in/jeanmarquesvieira/"
              target="_blank"
              rel="noopener noreferrer"
            >
              LinkedIn
            </a>
          </li>
        </ul>
        <div className="flex items-center space-x-2 sm:space-x-4 ml-auto">
          <img
            alt="BR"
            title="PT"
            onClick={() => handleLanguageChange("pt")}
            className="h-8 sm:h-9 hover:cursor-pointer hover:scale-110"
            src={br}
          />
          <img
            alt="EN"
            title="EN"
            onClick={() => handleLanguageChange("en")}
            className="h-8 sm:h-9 hover:cursor-pointer hover:scale-110 border-2 border-blue-500 rounded-md"
            src={uk}
          />
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
