import React from "react";
import { Link } from "react-router-dom";
import { useLanguage } from "../LanguageSource";
import cli from "../img/cli.png";
import br from "../img/br.png";
import uk from "../img/map.png";
import { Typewriter } from "react-simple-typewriter";

const Navbar = () => {
  const { currentLanguage, translations, changeLanguage } = useLanguage();

  const hello = translations[currentLanguage].hello;
  const myNameIs = translations[currentLanguage].myNameIs;

  const handleLanguageChange = (lang) => {
    if (lang !== currentLanguage) {
      changeLanguage(lang);
    }
  };

  const toggleLanguage = () => {
    return currentLanguage === "en" ? "pt" : "en";
  };

  return (
    <nav className="block px-3 sm:px-5 md:px-7 lg:px-9 py-2 sm:py-3 text-black bg-mainBlue overflow-hidden font-fira focus:outline-none">
      <div className="flex items-center justify-between focus:outline-none">
        <Link to="/">
          <img
            alt="Cli"
            className="h-7 sm:h-10 min-w-7 w-30 hover:cursor-pointer hover:scale-110 w-auto"
            src={cli}
          />
        </Link>
        <h1 className="text-xs sm:text-2xl md:text-3xl font-bold text-gray-800 mt-4 md:mt-0 justify-center flex-shrink-0">
          <div className="w-48 sm:w-64 md:w-72 overflow-hidden whitespace-nowrap">
            <Typewriter
              words={[hello, myNameIs]}
              loop={true}
              cursor
              cursorStyle="_"
              typeSpeed={150}
              deleteSpeed={50}
              delaySpeed={1000}
            />
          </div>
        </h1>
        <ul className="flex flex-nowrap font-bold text-sm sm:text-base justify-center sm:justify-end gap-4 sm:gap-10 lg:gap-14 flex-grow">
          <li className="block p-1 text-blue-gray-900">
            <a
              className="cursor-pointer whitespace-nowrap tracking-wider flex items-center transition-colors rounded-lg hover:text-cyan-300 hover:bg-sky-900 px-1 py-1 sm:px-4 sm:py-1"
              href="https://github.com/jeanmarquesvieira"
              target="_blank"
              rel="noopener noreferrer"
            >
              GitHub
            </a>
          </li>
          <li className="block p-1 text-blue-gray-900">
            <a
              className="cursor-pointer whitespace-nowrap tracking-wider flex items-center transition-colors rounded-lg hover:text-cyan-300 hover:bg-sky-900 px-1 py-1 sm:px-4 sm:py-1 sm:mr-5 mr-1"
              href="https://www.linkedin.com/in/jeanmarquesvieira/"
              target="_blank"
              rel="noopener noreferrer"
            >
              LinkedIn
            </a>
          </li>
        </ul>
        <div className="flex items-center sm:mx-4 md:mx-6 min-w-7">
          <img
            alt={toggleLanguage()}
            title={toggleLanguage() === "pt" ? "Português" : "English"}
            onClick={() => handleLanguageChange(toggleLanguage())}
            className="h-8 sm:h-10 hover:cursor-pointer hover:scale-110 border-2 border-transparent rounded-md"
            src={currentLanguage === "en" ? br : uk}
          />
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
