import React, { useState } from "react";
import { Link } from "react-router-dom";
import { useLanguage } from "../LanguageSource";
// import "../index.css";
import cli from "../img/cli.png";
import br from "../img/br.png";
import uk from "../img/uk.png";

const Navbar = () => {
  const { currentLanguage, translations, changeLanguage } = useLanguage();
  const [isPt, setIsPt] = useState(false);
  const [isEn, setIsEn] = useState(false);

  const handleLanguageChange = (lang) => {
    if (lang !== currentLanguage) {
      changeLanguage(lang);
    }
  };

  const toggleLanguage = () => {
    return currentLanguage === "en"
      ? "pt" && setIsPt(true) && setIsEn
      : "en" && setIsPt(false);
  };

  return (
    <nav className="block px-3 sm:px-5 md:px-7 lg:px-9 py-2 sm:py-3 text-black bg-bblue shadow-2xl overflow-hidden">
      <div className="flex items-center justify-between">
        <Link to="/">
          <img
            alt="Cli"
            className="h-8 sm:h-10 min-w-7 hover:cursor-pointer hover:scale-110 w-auto"
            src={cli}
          />
        </Link>
        <ul className="flex flex-nowrap flex-1 justify-end gap-2 sm:gap-4 lg:gap-6">
          <li className="block py-1 font-medium leading-normal text-blue-gray-900 ml-5">
            <Link
              className="cursor-pointer whitespace-nowrap text-base tracking-wider flex items-center transition-colors rounded-lg hover:text-cyan-300 hover:bg-sky-900"
              to="/projects"
            >
              {translations[currentLanguage].projects}
            </Link>
          </li>
          <li className="block p-1 font-medium leading-normal text-blue-gray-900">
            <a
              className="cursor-pointer whitespace-nowrap text-base tracking-wider flex items-center transition-colors rounded-lg hover:text-cyan-300 hover:bg-sky-900"
              href="https://github.com/jeanmarquesvieira"
              target="_blank"
              rel="noopener noreferrer"
            >
              GitHub
            </a>
          </li>
          <li className="block py-1 font-medium leading-normal text-blue-gray-900">
            <a
              className="cursor-pointer whitespace-nowrap text-base tracking-wider flex items-center transition-colors rounded-lg hover:text-cyan-300 hover:bg-sky-900 mr-6"
              href="https://www.linkedin.com/in/jeanmarquesvieira/"
              target="_blank"
              rel="noopener noreferrer"
            >
              LinkedIn
            </a>
          </li>
        </ul>
        <div className="flex items-center space-x-2 sm:x-4 md:x-6">
          {isPt && (
            <img
              alt={toggleLanguage()}
              title={toggleLanguage()}
              onClick={() => handleLanguageChange(toggleLanguage())}
              className="h-8 sm:h-10 hover:cursor-pointer hover:scale-110"
              src={currentLanguage === "br" ? uk : br}
            />
          )}{" "}
          {isPt === false && (
            <img
              alt={toggleLanguage()}
              title={toggleLanguage()}
              onClick={() => handleLanguageChange(toggleLanguage())}
              className="h-8 sm:h-10 hover:cursor-pointer hover:scale-110 border-2 border-transparent rounded-md"
              src={currentLanguage === "br" ? uk : br}
            />
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
