// src/components/SearchAndPlay.js
import React, { useState } from "react";
import axios from "axios";

const Spotify = () => {
  const [query, setQuery] = useState("");
  const [trackUri, setTrackUri] = useState("");
  const [message, setMessage] = useState("");

  const handleSearch = async () => {
    try {
      const response = await axios.get(
        `search?q=${query}`
      );
      setTrackUri(response.data.uri);
    } catch (error) {
      console.error("Search Error:", error);
      setMessage("Error occurred during search");
    }
  };

  const handlePlay = async () => {
    try {
      await axios.get(`play?uri=${trackUri}`);
      setMessage("Playback started");
    } catch (error) {
      console.error("Play Error:", error);
      setMessage("Error occurred during playback");
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Spotify Player</h1>
      <div className="mb-4">
        <input
          type="text"
          className="border p-2 w-full"
          placeholder="Search for a song"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
      </div>
      <div className="mb-4">
        <button
          className="bg-blue-500 text-white py-2 px-4 rounded"
          onClick={handleSearch}
        >
          Search
        </button>
      </div>
      {trackUri && (
        <div className="mb-4">
          <button
            className="bg-green-500 text-white py-2 px-4 rounded"
            onClick={handlePlay}
          >
            Play
          </button>
        </div>
      )}
      {message && <p>{message}</p>}
    </div>
  );
};

export default Spotify;
