// src/components/SearchAndPlay.js
import React, { useState } from "react";
import axios from "axios";
import { useLanguage } from "../LanguageSource";
import play from "../img/play.png";
import playDisabled from "../img/play_disabled.png";

const Spotify = () => {
  const { currentLanguage, translations } = useLanguage();
  const [query, setQuery] = useState("");
  const [trackUri, setTrackUri] = useState("");
  const [showMessage, setShowMessage] = useState(true);
  const [isError, setIsError] = useState("");

  const beingPlayed = translations[currentLanguage].beingPlayed;

  const handleSearch = async () => {
    try {
      const response = await axios.get(`/search?q=${query}`);
      setTrackUri(response.data.uri);
    } catch (error) {
      console.error("Search Error:", error);
      //   setMessage("Error occurred during search");
    }
  };

  const handlePlay = async () => {
    try {
      await axios.get(`/play?uri=${trackUri}`);
      setShowMessage(true);
      //   setMessage({translations[currentLanguage].beingPlayed});
    } catch (error) {
      console.error("Play Error:", error);
      //   setMessage("Error occurred during playback");
    }
  };

  return (
    <div className="container mx-auto p-4 pt-28 font-fira">
      <div className="border-2 rounded-lg p-20 mx-72">
        <h1 className="text-2xl font-bold mb-8 flex justify-center">
          Spotify Player
        </h1>
        <div className="mb-10 flex justify-center space-x-6">
          <input
            type="text"
            className="border p-2 rounded-lg w-5/12  justify-center"
            placeholder={translations[currentLanguage].searchForSong}
            value={query}
            onChange={(e) => setQuery(e.target.value)}
          />
        </div>
        <div className="mb-8 flex justify-center space-x-8">
          <button
            className="bg-mainBlue text-white py-2 min-w-32 rounded-lg hover:scale-110 hover:shadow-lg"
            onClick={handleSearch}
          >
            {translations[currentLanguage].searchBtn}
          </button>
          <img
            src={trackUri ? play : playDisabled}
            className={
              trackUri ? "h-10 hover:cursor-pointer animate-pulse" : "h-10"
            }
            onClick={handlePlay}
            title={translations[currentLanguage].play}
            alt="play"
          />
        </div>
        {showMessage && (
          <p className="flex justify-center items-center">{beingPlayed}</p>
        )}
      </div>
    </div>
  );
};

export default Spotify;
