import React, { useState } from "react";
import { Link } from "react-router-dom";
import { useLanguage } from "../LanguageSource";
import "../index.css";
import "../styles/navbar.css";
import terminal from "../img/terminal.png";
import br from "../img/br.png";
import uk from "../img/uk_.png";

const Navbar = () => {
  const { currentLanguage, translations } = useLanguage();
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };
  return (
    <nav className="block px-4 sm:px-6 md:px-8 lg:px-10 py-2 sm:py-3 md:py-4 lg:py-5 text-black bg-sky-600 shadow-2xl border-4 border-cyan-500/100 bg-opacity-95 backdrop-blur-2xl backdrop-saturate-200 overflow-hidden">
      <div className="flex items-center justify-between">
        <img
          alt="Terminal"
          className="h-10 sm:h-12 hover:cursor-pointer w-auto"
          src={terminal}
        />
        <ul className="flex flex-nowrap flex-1 justify-center my-1 sm:my-2 gap-2 sm:gap-4 lg:gap-6">
          <li className="block p-1 font-medium leading-normal text-blue-gray-900">
            <Link
              className="cursor-pointer whitespace-nowrap py-1.5 text-base tracking-wider flex items-center transition-colors rounded-lg px-2 hover:text-cyan-300 hover:bg-gray-700"
              to="/"
            >
              {translations[currentLanguage].home}
            </Link>
          </li>
          <li className="block p-1 font-medium leading-normal text-blue-gray-900">
            <Link
              className="cursor-pointer whitespace-nowrap py-1.5 text-base tracking-wider flex items-center transition-colors rounded-lg px-2 hover:text-cyan-300 hover:bg-gray-700"
              to="/projects"
            >
              {translations[currentLanguage].projects}
            </Link>
          </li>
          <li className="block p-1 font-medium leading-normal text-blue-gray-900">
            <a
              className="cursor-pointer whitespace-nowrap py-1.5 text-base tracking-wider flex items-center transition-colors rounded-lg px-2 hover:text-cyan-300 hover:bg-gray-700"
              href="https://github.com/jeanmarquesvieira"
              target="_blank"
              rel="noopener noreferrer"
            >
              GitHub
            </a>
          </li>
          <li className="block p-1 font-medium leading-normal text-blue-gray-900">
            <a
              className="cursor-pointer whitespace-nowrap py-1.5 text-base tracking-wider flex items-center transition-colors rounded-lg px-2 hover:text-cyan-300 hover:bg-gray-700"
              href="https://www.linkedin.com/in/jeanmarquesvieira/"
              target="_blank"
              rel="noopener noreferrer"
            >
              LinkedIn
            </a>
          </li>
          <li className="block p-1 font-medium leading-normal text-blue-gray-900">
            <Link
              className="cursor-pointer whitespace-nowrap py-1.5 text-base tracking-wider flex items-center transition-colors rounded-lg px-2 hover:text-cyan-300 hover:bg-gray-700"
              to="/"
            >
              {translations[currentLanguage].getInTouch}
            </Link>
          </li>
        </ul>
        <div className="flex items-center space-x-4 ml-auto">
          <img
            alt="BR"
            title="PT"
            className="h-10 sm:h-12 hover:cursor-pointer"
            src={br}
          />
          <img
            alt="EN"
            title="EN"
            className="h-10 sm:h-12 hover:cursor-pointer border-2 border-blue-500 rounded-md"
            src={uk}
          />
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
