import "../index.css";
import jmv from "../img/jmv.png";
import { useLanguage } from "../LanguageSource";
import React from "react";
import TechModal from "./TechModal";
import SoftSkillsModal from "./SoftSkillsModal";
import { Typewriter } from "react-simple-typewriter";

const Home = () => {
  const { currentLanguage, translations } = useLanguage();

  let hello = translations[currentLanguage].hello;
  let myNameIs = translations[currentLanguage].myNameIs;

  return (
    <>
      <div className="bg-gray-100 min-h-screen flex items-center justify-center font-fira">
        <div className="flex flex-col md:flex-row w-full max-w-5xl mx-auto p-4">
          <div className="flex justify-center md:justify-start md:w-1/3 mb-4 md:mb-0">
            <div className="relative bottom-20 h-40 w-32 md:h-64 md:w-52 rounded-full overflow-hidden">
              <img
                src={jmv}
                alt="jmv"
                className="absolute inset-0 h-full w-full object-cover"
              />
            </div>
          </div>
          <div className="flex flex-col relative bottom-20 md:ml-4 md:w-2/3">
            <p className="text-sm sm:text-md md:text-lg text-gray-700 mt-6 md:mt-10 text-justify md:tracking-wider tracking-tight flex-grow pb-20 fadeinleft">
              {translations[currentLanguage].description}
            </p>
            <div className="flex space-x-4 mt-4 md:mt-8">
              <div className="w-full max-w-xs">
                <TechModal />
              </div>
              <div className="w-full max-w-xs">
                <SoftSkillsModal />
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Home;
